@pytest.mark.parametrize( "move_tuple, expected_usi", [ ((2, 2, 3, 2, False), "7c7d"), # Corrected: (2,2)=7c, (3,2)=7d ((7, 7, 8, 6, True), "2h3i+"), # Corrected: (7,7)=2h, (8,6)=3i ((None, None, 4, 4, PieceType.PAWN), "P*5e"), # Drop Pawn to 5e ((None, None, 0, 0, PieceType.ROOK), "R*9a"), # Drop Rook to 9a ((6, 7, 5, 7, False), "2g2f"), # Corrected: (6,7)=2g, (5,7)=2f ], ) def test_shogi_move_to_usi_valid( mapper: PolicyOutputMapper, move_tuple: MoveTuple, expected_usi ): assert mapper.shogi_move_to_usi(move_tuple) == expected_usi
@pytest.mark.parametrize( "invalid_move_tuple", [ # ((0, 0, 0, 0, False)), # This is a valid tuple structure, _usi_sq would work. PolicyOutputMapper skips same-square. # ((1, 1, 1, 1, True)), # Same as above. ( (None, None, 0, 0, PieceType.KING) ), # Cannot drop King - _get_usi_char_for_drop raises ValueError ( (0, 1, 2, 3, "False") ), # Invalid promote type - isinstance check in shogi_move_to_usi ((0, 1, 2, 3, 4)), # Invalid structure / promote type ("7g7f"), # Not a tuple - shogi_move_to_usi expects a tuple ((0, 0, 9, 0, False)), # Invalid to_sq for _usi_sq ], ) def test_shogi_move_to_usi_invalid(mapper: PolicyOutputMapper, invalid_move_tuple): with pytest.raises( (ValueError, TypeError) ): # Allow TypeError for cases like passing a string mapper.shogi_move_to_usi(invalid_move_tuple) # type: ignore
