@pytest.mark.parametrize( "invalid_usi_move", [ # ("7g7g"), # This is a valid USI string, though the move might be illegal in a game. ("P*5e+"), # Drop moves cannot have promotion ("K*5e"), # Cannot drop King (invalid piece char for drop) ("7g10f"), # Invalid 'to_sq' rank ("1234"), # Malformed board move ("L*1j"), # Invalid 'to_sq' rank for drop (12345), # Not a string ("P*5X"), # Invalid rank char in drop ("X*5e"), # Invalid piece char for drop ("7g7f++"), # Double promotion ("7g7"), # Too short ("B*"), # Missing target square for drop ("*5e"), # Missing piece for drop ], ) def test_usi_to_shogi_move_invalid(mapper: PolicyOutputMapper, invalid_usi_move): with pytest.raises((ValueError, TypeError)): # Allow TypeError for non-string input mapper.usi_to_shogi_move(invalid_usi_move) # type: ignore
